
public class OperatorMain09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean bo = true;
		
		System.out.println("현재 bo의 값은 " + bo + "입니다.");
		bo = !bo;
		System.out.println("현재 bo의 값은 " + bo + "입니다.");
		bo = !bo;
		System.out.println("현재 bo의 값은 " + bo + "입니다.");
		
	}

}
