
public class StatementMain02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 20;
		
		if (age == 20) {
			System.out.println("20살 입니다.");
		}
		if (age < 20) {
			System.out.println("아직 20살이 안됐네요.");
		}
		if (age > 19) {
			System.out.println("19세 이상이시면 성인입니다.");
		}
	}
}
